/****************************************************
*   This file contains all the default values for   *
*   the php-logger.inc file.  Only change           *
*   these values if you know what you are doing.    *
****************************************************/

// Add the PHP SYSLOG variables to the predefined constants list
define_syslog_variables();

if(!defined('LOG_LOCAL6'))define('LOG_LOCAL6',6);

//Defoult logging type: message OR local OR syslog
define("DEFAULT_LOG_TYPE",'local'); 

//Default DEBUG set to 3 or 6
define("DEFAULT_DEBUG_SETTING",3);

// Define the syslog facility that error messages generated by the logger itself should go to.
define ("DEFAULT_LOGGER_LOG_FACILITY", LOG_LOCAL6);

// Default log message severity
define ("DEFAULT_MSG_SEVERITY", LOG_INFO);

// Default log message category
define ("DEFAULT_MSG_CATEGORY", "OTHER");

// Default message threshold
define ("DEFAULT_THRESH", LOG_INFO);

// How many messages do we queue before dumping to the log?
define ("DEFAULT_MAX_QUEUE_SIZE", 250);

// At what severity do we flush the log automatically?
define ("DEFAULT_ERROR_CONDITION_TRIGGER_THRESH", LOG_INFO);

// When we are in error condition what severity level do we output until the queue is empty?
define ("DEFAULT_ERROR_CONDITION_THRESH", LOG_INFO);

// Default queueing status 
define ("DEFAULT_QUEUE_MODE", FALSE);

// Defaults for local file logging
define ("DEFAULT_LOCAL_LOGFILE_NAME", "Ktqueue.log");
define ("DEFAULT_LOCAL_LOGFILE_PATH", realpath(dirname(__FILE__) . '/../../../logs'));
define ("DEFAULT_LOCAL_METHOD", "APPEND");

// Defaults for Emailing logs entries
define ("DEFAULT_EMAIL_RECIPIENT", "KT Engineering <kt-engineering@knowledgetree.com>");
define ("DEFAULT_EMAIL_SENDER", "Logger <kt-engineering@knowledgetree.com");
define ("DEFAULT_EMAIL_SUBJECT", "LOG MESSAGE: ".date_default_timezone_get()); // The calling application is appended to the end of this.

// Defaults for syslog
define ("DEFAULT_SYSLOG_FACILITY", LOG_LOCAL6);

// Message Severities
define ("EMERG", LOG_EMERG);
define ("ALERT", LOG_ALERT);
define ("CRITICAL", LOG_CRIT);
define ("ERROR", LOG_ERR);
define ("WARNING", LOG_WARNING);
define ("NOTICE", LOG_NOTICE);
define ("INFO", LOG_INFO);
define ("DEBUG", LOG_DEBUG);

define ("MESSAGE_CATEGORIES_LIST", "LDAP,DATA,SIS,LOGIC,FTP,LOGS,SQL,OTHER,INTERNAL,DEBUG");
define ("SYSLOG_LEVELS_LIST", "EMERG,ALERT,CRITICAL,ERROR,WARNING,NOTICE,INFO,DEBUG");

define ("MSG_FORCE", 0);
define ("MSG_CATEGORY", 1);
define ("MSG_SEVERITY", 2);
define ("MSG_MSG", 3);
define ("MSG_TIME", 4);

if(!defined('LOG_MODES_LIST'))define('LOG_MODES_LIST',NULL);
?>
